# X12 envelope defaults for all trading partners

syntax = { 
        'charset'    : 'cp1252',
        'record_sep' :'~',
        'field_sep'  :'*',
        'sfield_sep' :'>',
        'ISA11'      :'U',  # U = ACS X12
        'GS07'       :'X',  # X = X12
        'version'    :'00403',
        'ISA02'      :'          ',
        'ISA04'      :'          ',
        'ISA05'      :'12',
        'ISA06'      :'7048710990',     # from TP: Badger Sportswear
        }
