# Example: Boilerplate info for new trading partner syntax setup 

syntax = {
    'add_crlfafterrecord_sep':'',       # additional character(s) to use after segment separators. 
                                        # default is '\r\n'; this gives nice formatting - but is not always accepted.
                                        
    'sfield_sep':'>',       #sub field separator
    'field_sep':'*',        #field separator
    'record_sep':"~",       #record/segment separator
    'merge': True,          #merge x12 transactions in one ISA-GS-GE-IEA (if for same receiver,sender, messagetype, etc). Default: True
    
    'charset'    :'cp1252',             #charset used by partner
    'version'  : '00401',               #ISA version to use.
    'ISA05'    : 'XX',                  #communication qualifier sender
    'ISA06'    : 'ISA SENDER',          #sender in ISA envelope
    'ISA07'    : 'ZZ',                  #communication qualifier reciever
    'ISA08'    : 'ISA RECEIVER',        #receiver in ISA envelope
    'ISA11'    : 'U',      
    'ISA15'    : 'P',                   #test-indicator: T=test, P=production
    'GS02'     : 'GS SENDER',           #sender in GS envelope
    'GS03'     : 'GS RECEIVER',         #receiver in GS envelope    'version': '00403',     #ISA version.
    
    #
    # default values from grammar.py (do not change grammar.py, it may get overwritten at upgrade time)
    #
    # 'ISA01': '00',
    # 'ISA03': '00',
    # 'ISA05': '01',
    # 'ISA07': '01',
    # 'ISA11': 'U',
    # 'ISA14': '1',
    # 'ISA15': 'P',
    # 'GS02': <frompartner QUERY>,
    # 'GS03': <topartner QUERY>,
    # 'GS07': 'X',
    }